import{_ as e,o as i,c as a,d as o}from"./app.cf111618.js";const g=JSON.parse('{"title":"2022-6-26","description":"","frontmatter":{"title":"2022-6-26","date":"2022-06-26"},"headers":[{"level":2,"title":"1. v-show \u548C v-if \u7684\u533A\u522B","slug":"_1-v-show-\u548C-v-if-\u7684\u533A\u522B"},{"level":2,"title":"2. async \u3001await \u4F7F\u7528\u6CE8\u610F\u4E8B\u9879","slug":"_2-async-\u3001await-\u4F7F\u7528\u6CE8\u610F\u4E8B\u9879"},{"level":2,"title":"3. \u7B80\u8FF0 promise \u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5","slug":"_3-\u7B80\u8FF0-promise-\u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5"},{"level":2,"title":"4. Cookie\u3001sessionStorage\u3001localStorage \u7684\u533A\u522B","slug":"_4-cookie\u3001sessionstorage\u3001localstorage-\u7684\u533A\u522B"},{"level":2,"title":"5. \u8BF7\u4ECB\u7ECD\u4E00\u4E0B require \u7684\u6A21\u5757\u52A0\u8F7D\u673A\u5236","slug":"_5-\u8BF7\u4ECB\u7ECD\u4E00\u4E0B-require-\u7684\u6A21\u5757\u52A0\u8F7D\u673A\u5236"}],"relativePath":"\u6BCF\u65E5/2022-06-26.md","lastUpdated":null}'),l={name:"\u6BCF\u65E5/2022-06-26.md"},r=o('<h1 id="_2022-6-26" tabindex="-1">2022-6-26 <a class="header-anchor" href="#_2022-6-26" aria-hidden="true">#</a></h1><h2 id="_1-v-show-\u548C-v-if-\u7684\u533A\u522B" tabindex="-1">1. v-show \u548C v-if \u7684\u533A\u522B <a class="header-anchor" href="#_1-v-show-\u548C-v-if-\u7684\u533A\u522B" aria-hidden="true">#</a></h2><ul><li><p>v-show \u548C v-if \u90FD\u662F true \u7684\u65F6\u5019\u663E\u793A\uFF0Cfalse \u7684\u65F6\u5019\u9690\u85CF</p></li><li><p>\u4F46\u662F\uFF1Afalse \u7684\u60C5\u51B5\u4E0B\uFF0Cv-show \u662F\u91C7\u7528\u7684 display:none</p></li><li><p>v-if \u76F4\u63A5\u4ECE DOM \u6811\u4E0A\u79FB\u9664,\u5982\u679C\u9700\u8981\u9891\u7E41\u5207\u6362\u663E\u793A\u9690\u85CF\u9700\u8981\u4F7F\u7528 v-show</p></li></ul><h2 id="_2-async-\u3001await-\u4F7F\u7528\u6CE8\u610F\u4E8B\u9879" tabindex="-1">2. async \u3001await \u4F7F\u7528\u6CE8\u610F\u4E8B\u9879 <a class="header-anchor" href="#_2-async-\u3001await-\u4F7F\u7528\u6CE8\u610F\u4E8B\u9879" aria-hidden="true">#</a></h2><ul><li><p>\u5982\u679C\u5728 function \u4E2D\u4F7F\u7528\u4E86 await\uFF0C\u5219 function \u5FC5\u987B\u88AB async \u4FEE\u9970(<strong>\u79BB await \u6700\u8FD1\u7684\u4E00\u4E2A\u51FD\u6570\u8FDB\u884C\u4FEE\u9970</strong>)</p></li><li><p>\u5728 async \u65B9\u6CD5\u4E2D\uFF0C\u7B2C\u4E00\u4E2A await \u4E4B\u524D\u7684\u4EE3\u7801\u4F1A\u540C\u6B65\u6267\u884C\uFF0Cawait \u4E4B\u540E\u7684\u4EE3\u7801\u4F1A\u5F02\u6B65\u6267\u884C</p></li></ul><h2 id="_3-\u7B80\u8FF0-promise-\u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5" tabindex="-1">3. \u7B80\u8FF0 promise \u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5 <a class="header-anchor" href="#_3-\u7B80\u8FF0-promise-\u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5" aria-hidden="true">#</a></h2><ul><li>\u4E09\u79CD\u72B6\u6001:pending(\u672A\u51B3\u5B9A), fulfilled(\u5C65\u884C), rejected(\u62D2\u7EDD)\u3002\u540C\u4E00\u65F6\u95F4\u53EA\u80FD\u5B58\u5728\u4E00\u79CD\u72B6\u6001\uFF0C\u4E14\u72B6\u6001\u4E00\u65E6\u6539\u53D8\u5C31\u4E0D\u80FD\u518D\u53D8\u3002</li><li>\u521D\u59CB\u5316: pending</li><li>\u5F53\u6210\u529F\u65F6: pending \u53D8\u4E3A fulfilled</li><li>\u5F53\u5931\u8D25\u65F6: pending \u53D8\u4E3A rejected</li><li>\u51E0\u4E2A\u65B9\u6CD5</li></ul><ol><li><code>Promise.prototype.then</code> \u7528\u6765\u6307\u5B9A\u56DE\u8C03\u51FD\u6570</li><li><code>Promise.prototype.catch</code> \u53EA\u80FD\u7528\u6765\u6307\u5B9A\u5931\u8D25\u7684\u56DE\u8C03\u51FD\u6570</li><li><code>Promise.resolve</code> \u8FD4\u56DE\u4E00\u4E2A\u6210\u529F/\u5931\u8D25\u7684 promise \u5BF9\u8C61</li><li><code>Promise.reject</code> \u8FD4\u56DE\u4E00\u4E2A\u5931\u8D25\u7684 promise \u5BF9\u8C61</li><li><code>Promise.all</code> \u8FD4\u56DE\u7684\u662F\u4E00\u4E2A\u65B0\u7684 promise \u5BF9\u8C61, \u53EA\u6709\u6240\u6709\u7684 promise \u90FD\u6210\u529F\u624D\u6210\u529F, \u53EA\u8981\u6709\u4E00\u4E2A\u5931\u8D25\u4E86\u5C31\u76F4\u63A5\u5931\u8D25</li><li><code>Promise.race</code> \u8FD4\u56DE\u4E00\u4E2A\u65B0\u7684 promise\uFF0C\u7B2C\u4E00\u4E2A\u5B8C\u6210\u7684 promise \u7684\u7ED3\u679C\u72B6\u6001\u5C31\u662F\u6700\u7EC8\u7684\u7ED3\u679C\u72B6\u6001</li></ol><h2 id="_4-cookie\u3001sessionstorage\u3001localstorage-\u7684\u533A\u522B" tabindex="-1">4. Cookie\u3001sessionStorage\u3001localStorage \u7684\u533A\u522B <a class="header-anchor" href="#_4-cookie\u3001sessionstorage\u3001localstorage-\u7684\u533A\u522B" aria-hidden="true">#</a></h2><ol><li>Cookie \u5728\u6D4F\u89C8\u5668\u4E0E\u670D\u52A1\u5668\u4E4B\u95F4\u6765\u56DE\u4F20\u9012,sessionStorage \u548C localStorage \u4EC5\u5728\u672C\u5730\u4FDD\u5B58</li><li>Cookie \u6709\u6709\u6548\u671F,\u8FC7\u671F\u4F1A\u5220\u9664,localStorage \u59CB\u7EC8\u6709\u6548, sessionStorage \u5F53\u524D\u6D4F\u89C8\u5668\u7A97\u53E3\u5173\u95ED\u524D\u6709\u6548</li><li>Cookie \u6570\u636E\u4E0D\u80FD\u8D85\u8FC7 4k, localStorage \u548C sessionStorage \u6709 5M \u6216\u8005\u66F4\u5927</li><li>Cookie \u548C localStorage \u5728\u6240\u6709\u540C\u6E90\u7A97\u53E3\u5171\u4EAB, sessionStorage \u4E0D\u5728\u4E0D\u540C\u7684\u6D4F\u89C8\u5668\u7A97\u53E3\u5171\u4EAB</li></ol><h2 id="_5-\u8BF7\u4ECB\u7ECD\u4E00\u4E0B-require-\u7684\u6A21\u5757\u52A0\u8F7D\u673A\u5236" tabindex="-1">5. \u8BF7\u4ECB\u7ECD\u4E00\u4E0B require \u7684\u6A21\u5757\u52A0\u8F7D\u673A\u5236 <a class="header-anchor" href="#_5-\u8BF7\u4ECB\u7ECD\u4E00\u4E0B-require-\u7684\u6A21\u5757\u52A0\u8F7D\u673A\u5236" aria-hidden="true">#</a></h2><ul><li>require \u4F18\u5148\u52A0\u8F7D\u7F13\u5B58\u4E2D\u7684\u6A21\u5757\u3002\u540C\u4E00\u4E2A\u6A21\u5757\u7B2C\u4E00\u6B21 require \u4E4B\u540E\uFF0C\u5C31\u4F1A\u7F13\u5B58\u4E00\u4EFD\uFF0C\u4E0B\u4E00\u6B21 require \u65F6\u5C31\u76F4\u63A5\u4ECE\u7F13\u5B58\u4E2D\u53BB\u53D6\u3002</li><li>\u5982\u679C\u662F\u52A0\u8F7D\u6838\u5FC3\u6A21\u5757\uFF0C\u76F4\u63A5\u4ECE\u5185\u5B58\u4E2D\u52A0\u8F7D\uFF0C\u5E76\u7F13\u5B58</li><li>\u5982\u679C\u662F\u76F8\u5BF9\u8DEF\u5F84\uFF0C\u5219\u6839\u636E\u8DEF\u5F84\u52A0\u8F7D\u81EA\u5B9A\u4E49\u6A21\u5757\uFF0C\u5E76\u7F13\u5B58</li><li>\u5982\u679C\u4E0D\u662F\u81EA\u5B9A\u4E49\u6A21\u5757\uFF0C\u4E5F\u4E0D\u662F\u6838\u5FC3\u6A21\u5757\uFF0C\u5219\u52A0\u8F7D\u7B2C\u4E09\u65B9\u6A21\u5757<br><a href="https://itcn.blog/p/1409261229.html" target="_blank" rel="noopener noreferrer">\u53C2\u8003\u94FE\u63A5</a></li></ul>',12),s=[r];function t(n,c,d,h,_,p){return i(),a("div",null,s)}var f=e(l,[["render",t]]);export{g as __pageData,f as default};
