import{_ as e,c as l,o as i,a}from"./app.5cb5f4a6.js";const b=JSON.parse('{"title":"2022-6-15","description":"","frontmatter":{"title":"2022-6-15","date":"2022-06-15"},"headers":[{"level":2,"title":"1. map \u4E0E forEach \u7684\u533A\u522B","slug":"_1-map-\u4E0E-foreach-\u7684\u533A\u522B"},{"level":2,"title":"2. \u7B80\u8FF0 promise \uFF0C\u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5","slug":"_2-\u7B80\u8FF0-promise-\uFF0C\u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5"},{"level":2,"title":"3. call() \u3001apply() \u548C bind()\u7684\u533A\u522B\uFF1F","slug":"_3-call-\u3001apply-\u548C-bind-\u7684\u533A\u522B\uFF1F"},{"level":2,"title":"4. js \u662F\u5355\u7EBF\u7A0B\u7684\uFF0C\u4E3A\u4EC0\u4E48 js \u80FD\u6709\u5F02\u6B65\u4EFB\u52A1","slug":"_4-js-\u662F\u5355\u7EBF\u7A0B\u7684\uFF0C\u4E3A\u4EC0\u4E48-js-\u80FD\u6709\u5F02\u6B65\u4EFB\u52A1"},{"level":2,"title":"5. JavaScript \u4E2D\u641C\u7D22\u6570\u7EC4\u7684\u65B9\u6CD5","slug":"_5-javascript-\u4E2D\u641C\u7D22\u6570\u7EC4\u7684\u65B9\u6CD5"}],"relativePath":"\u6BCF\u65E5\u9762\u8BD5\u9898/2022-06-15.md","lastUpdated":null}'),o={name:"\u6BCF\u65E5\u9762\u8BD5\u9898/2022-06-15.md"},p=a('<h1 id="_2022-6-15" tabindex="-1">2022-6-15 <a class="header-anchor" href="#_2022-6-15" aria-hidden="true">#</a></h1><h2 id="_1-map-\u4E0E-foreach-\u7684\u533A\u522B" tabindex="-1">1. map \u4E0E forEach \u7684\u533A\u522B <a class="header-anchor" href="#_1-map-\u4E0E-foreach-\u7684\u533A\u522B" aria-hidden="true">#</a></h2><ul><li><p>forEach(): \u9488\u5BF9\u6BCF\u4E00\u4E2A\u5143\u7D20\u6267\u884C\u63D0\u4F9B\u7684\u51FD\u6570</p></li><li><p>map(): \u521B\u5EFA\u4E00\u4E2A\u65B0\u7684\u6570\u7EC4\uFF0C\u5176\u4E2D\u6BCF\u4E00\u4E2A\u5143\u7D20\u7531\u8C03\u7528\u6570\u7EC4\u4E2D\u7684\u6BCF\u4E00\u4E2A\u5143\u7D20\u6267\u884C\u63D0\u4F9B\u7684\u51FD\u6570\u5F97\u6765</p></li><li><p>\u76F8\u540C\u70B9:</p><p>\u90FD\u662F\u5FAA\u73AF\u904D\u5386\u6570\u7EC4\u4E2D\u7684\u6BCF\u4E00\u9879,\u80FD\u7528 forEach()\u505A\u5230\u7684\uFF0Cmap()\u540C\u6837\u53EF\u4EE5\u3002\u53CD\u8FC7\u6765\u4E5F\u662F\u5982\u6B64\u3002forEach \u548C map \u65B9\u6CD5\u91CC\u6BCF\u6B21\u6267\u884C\u533F\u540D\u51FD\u6570\u90FD\u652F\u6301 3 \u4E2A\u53C2\u6570\uFF0C\u53C2\u6570\u5206\u522B\u662F item\uFF08\u5F53\u524D\u6BCF\u4E00\u9879), index\uFF08\u7D22\u5F15\u503C\uFF09\uFF0Carr\uFF08\u539F\u6570\u7EC4\uFF09</p></li><li><p>\u4E0D\u540C\u70B9</p><ol><li>map()\u4F1A\u5206\u914D\u5185\u5B58\u7A7A\u95F4\u5B58\u50A8\u65B0\u6570\u7EC4\u5E76\u8FD4\u56DE\uFF0CforEach()\u4E0D\u4F1A\u8FD4\u56DE\u6570\u636E\u3002</li><li>forEach()\u5141\u8BB8 callback \u66F4\u6539\u539F\u59CB\u6570\u7EC4\u7684\u5143\u7D20\u3002map()\u8FD4\u56DE\u65B0\u7684\u6570\u7EC4\u3002</li></ol></li></ul><h2 id="_2-\u7B80\u8FF0-promise-\uFF0C\u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5" tabindex="-1">2. \u7B80\u8FF0 promise \uFF0C\u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5 <a class="header-anchor" href="#_2-\u7B80\u8FF0-promise-\uFF0C\u51E0\u79CD\u72B6\u6001\u8DDF\u65B9\u6CD5" aria-hidden="true">#</a></h2><ul><li>\u4E09\u79CD\u72B6\u6001:pending(\u672A\u51B3\u5B9A), resolved(\u5C65\u884C), rejected(\u62D2\u7EDD)\u3002\u540C\u4E00\u65F6\u95F4\u53EA\u80FD\u5B58\u5728\u4E00\u79CD\u72B6\u6001\uFF0C\u4E14\u72B6\u6001\u4E00\u65E6\u6539\u53D8\u5C31\u4E0D\u80FD\u518D\u53D8\u3002 <ul><li>\u521D\u59CB\u5316: pending</li><li>\u5F53\u6210\u529F\u65F6: pending \u53D8\u4E3A resolved</li><li>\u5F53\u5931\u8D25\u65F6: pending \u53D8\u4E3A rejected</li></ul></li><li>\u51E0\u4E2A\u65B9\u6CD5</li></ul><ol><li><code>Promise.prototype.then</code> \u7528\u6765\u6307\u5B9A\u56DE\u8C03\u51FD\u6570</li><li><code>Promise.prototype.catch</code> \u53EA\u80FD\u7528\u6765\u6307\u5B9A\u5931\u8D25\u7684\u56DE\u8C03\u51FD\u6570</li><li><code>Promise.resolve</code> \u8FD4\u56DE\u4E00\u4E2A\u6210\u529F/\u5931\u8D25\u7684 promise \u5BF9\u8C61</li><li><code>Promise.reject</code> \u8FD4\u56DE\u4E00\u4E2A\u5931\u8D25\u7684 promise \u5BF9\u8C61</li><li><code>Promise.all</code> \u8FD4\u56DE\u7684\u662F\u4E00\u4E2A\u65B0\u7684 promise \u5BF9\u8C61, \u53EA\u6709\u6240\u6709\u7684 promise \u90FD\u6210\u529F\u624D\u6210\u529F, \u53EA\u8981\u6709\u4E00\u4E2A\u5931\u8D25\u4E86\u5C31\u76F4\u63A5\u5931\u8D25</li><li><code>Promise.race</code> \u8FD4\u56DE\u4E00\u4E2A\u65B0\u7684 promise\uFF0C\u7B2C\u4E00\u4E2A\u5B8C\u6210\u7684 promise \u7684\u7ED3\u679C\u72B6\u6001\u5C31\u662F\u6700\u7EC8\u7684\u7ED3\u679C\u72B6\u6001</li></ol><h2 id="_3-call-\u3001apply-\u548C-bind-\u7684\u533A\u522B\uFF1F" tabindex="-1">3. call() \u3001apply() \u548C bind()\u7684\u533A\u522B\uFF1F <a class="header-anchor" href="#_3-call-\u3001apply-\u548C-bind-\u7684\u533A\u522B\uFF1F" aria-hidden="true">#</a></h2><ul><li>\u5171\u540C\u70B9\uFF1A <ul><li>\u6539\u53D8\u51FD\u6570\u8FD0\u884C\u65F6\u7684 this \u6307\u5411</li></ul></li><li>\u533A\u522B\uFF1A</li></ul><ol><li>call\u3001apply \u4E0E bind \u7684\u533A\u522B <blockquote><p>call \u548C apply \u6539\u53D8\u4E86\u51FD\u6570\u7684 this \u4E0A\u4E0B\u6587\u540E\u4FBF\u6267\u884C\u8BE5\u51FD\u6570,\u800C bind \u5219\u662F\u8FD4\u56DE\u6539\u53D8\u4E86\u4E0A\u4E0B\u6587\u540E\u7684\u4E00\u4E2A\u51FD\u6570\u3002</p></blockquote></li><li>call\u3001apply \u7684\u533A\u522B <blockquote><p>\u4ED6\u4EEC\u4FE9\u4E4B\u95F4\u7684\u5DEE\u522B\u5728\u4E8E\u53C2\u6570\u7684\u533A\u522B\uFF0Ccall \u548C apply \u7684\u7B2C\u4E00\u4E2A\u53C2\u6570\u90FD\u662F\u8981\u6539\u53D8\u4E0A\u4E0B\u6587\u7684\u5BF9\u8C61\uFF0C\u800C call \u4ECE\u7B2C\u4E8C\u4E2A\u53C2\u6570\u5F00\u59CB\u4EE5\u53C2\u6570\u5217\u8868\u7684\u5F62\u5F0F\u5C55\u73B0\uFF0Capply \u5219\u662F\u628A\u9664\u4E86\u6539\u53D8\u4E0A\u4E0B\u6587\u5BF9\u8C61\u7684\u53C2\u6570\u653E\u5728\u4E00\u4E2A\u6570\u7EC4\u91CC\u9762\u4F5C\u4E3A\u5B83\u7684\u7B2C\u4E8C\u4E2A\u53C2\u6570\u3002</p></blockquote></li></ol><p><a href="https://juejin.cn/post/6844903567967387656" target="_blank" rel="noopener noreferrer">\u53C2\u8003\u94FE\u63A5</a></p><h2 id="_4-js-\u662F\u5355\u7EBF\u7A0B\u7684\uFF0C\u4E3A\u4EC0\u4E48-js-\u80FD\u6709\u5F02\u6B65\u4EFB\u52A1" tabindex="-1">4. js \u662F\u5355\u7EBF\u7A0B\u7684\uFF0C\u4E3A\u4EC0\u4E48 js \u80FD\u6709\u5F02\u6B65\u4EFB\u52A1 <a class="header-anchor" href="#_4-js-\u662F\u5355\u7EBF\u7A0B\u7684\uFF0C\u4E3A\u4EC0\u4E48-js-\u80FD\u6709\u5F02\u6B65\u4EFB\u52A1" aria-hidden="true">#</a></h2><ul><li>JavaScript \u662F\u811A\u672C\u8BED\u8A00\uFF0C\u5B83\u9700\u8981\u5728\u4E00\u4E2A\u5BBF\u4E3B\u73AF\u5883\u91CC\u624D\u80FD\u8FD0\u884C,\u4F8B\u5982\u6D4F\u89C8\u5668\u3002</li><li>\u867D\u8BF4 JavaScript \u662F\u5355\u7EBF\u7A0B\u7684\uFF0C\u4F46\u6D4F\u89C8\u5668\u4E0D\u662F\u3002</li><li>js \u901A\u8FC7<code>\u4E8B\u4EF6\u5FAA\u73AF</code>\u5B9E\u73B0\u5F02\u6B65\u64CD\u4F5C\u3002 <blockquote><p>\u4E3B\u7EBF\u7A0B\u9047\u5230\u5F02\u6B65\u4EFB\u52A1\u5C31\u4F1A\u653E\u5230\u4E8B\u4EF6\u961F\u5217\u4E2D\uFF0C\u5E76\u7531\u5BF9\u5E94\u7684\u7EBF\u7A0B\u53BB\u6267\u884C\uFF0C\u6267\u884C\u5B8C\u6210\u540E\u4F1A\u8FD4\u56DE\u7ED9\u4E3B\u7EBF\u7A0B\u3002</p></blockquote></li></ul><h2 id="_5-javascript-\u4E2D\u641C\u7D22\u6570\u7EC4\u7684\u65B9\u6CD5" tabindex="-1">5. JavaScript \u4E2D\u641C\u7D22\u6570\u7EC4\u7684\u65B9\u6CD5 <a class="header-anchor" href="#_5-javascript-\u4E2D\u641C\u7D22\u6570\u7EC4\u7684\u65B9\u6CD5" aria-hidden="true">#</a></h2><ol><li><p>Array.prototype.includes()</p><blockquote><p>\u7528\u6765\u5224\u65AD\u4E00\u4E2A\u6570\u7EC4\u662F\u5426\u5305\u542B\u4E00\u4E2A\u6307\u5B9A\u7684\u503C\uFF0C\u5982\u679C\u5305\u542B\u5219\u8FD4\u56DE true\uFF0C\u5426\u5219\u8FD4\u56DE false\u3002</p></blockquote></li><li><p>Array.prototype.indexOf()</p><blockquote><p>\u8FD4\u56DE\u6307\u5B9A\u5143\u7D20\u5728\u6570\u7EC4\u4E2D\u7684\u7B2C\u4E00\u4E2A\u7D22\u5F15\uFF0C\u5982\u679C\u4E0D\u5B58\u5728\uFF0C\u5219\u8FD4\u56DE-1\u3002</p></blockquote></li><li><p>Array.prototype.lastIndexOf()</p><blockquote><p>\u8FD4\u56DE\u6307\u5B9A\u5143\u7D20\u5728\u6570\u7EC4\u4E2D\u7684\u6700\u540E\u4E00\u4E2A\u7684\u7D22\u5F15\uFF0C\u5982\u679C\u4E0D\u5B58\u5728\u5219\u8FD4\u56DE -1\u3002\u4ECE\u6570\u7EC4\u7684\u540E\u9762\u5411\u524D\u67E5\u627E\u3002</p></blockquote></li><li><p>Array.prototype.some()</p><blockquote><p>\u6D4B\u8BD5\u6570\u7EC4\u4E2D\u662F\u4E0D\u662F\u81F3\u5C11\u6709 1 \u4E2A\u5143\u7D20\u901A\u8FC7\u4E86\u88AB\u63D0\u4F9B\u7684\u51FD\u6570\u6D4B\u8BD5\u3002\u5B83\u8FD4\u56DE\u7684\u662F\u4E00\u4E2A Boolean \u7C7B\u578B\u7684\u503C\u3002</p></blockquote></li><li><p>Array.prototype.every()</p><blockquote><p>\u6D4B\u8BD5\u4E00\u4E2A\u6570\u7EC4\u5185\u7684\u6240\u6709\u5143\u7D20\u662F\u5426\u90FD\u80FD\u901A\u8FC7\u67D0\u4E2A\u6307\u5B9A\u51FD\u6570\u7684\u6D4B\u8BD5\u3002\u5B83\u8FD4\u56DE\u4E00\u4E2A\u5E03\u5C14\u503C\u3002</p></blockquote></li><li><p>Array.prototype.filter()</p><blockquote><p>\u521B\u5EFA\u4E00\u4E2A\u65B0\u6570\u7EC4, \u5305\u542B\u901A\u8FC7\u6240\u63D0\u4F9B\u51FD\u6570\u5B9E\u73B0\u7684\u6D4B\u8BD5\u7684\u6240\u6709\u5143\u7D20\u3002</p></blockquote></li><li><p>Array.prototype.find()</p><blockquote><p>\u8FD4\u56DE\u6570\u7EC4\u4E2D\u6EE1\u8DB3\u63D0\u4F9B\u7684\u6D4B\u8BD5\u51FD\u6570\u7684\u7B2C\u4E00\u4E2A\u5143\u7D20\u7684\u503C\u3002\u5426\u5219\u8FD4\u56DE undefined\u3002</p></blockquote></li><li><p>Array.prototype.findIndex()</p><blockquote><p>\u8FD4\u56DE\u6570\u7EC4\u4E2D\u6EE1\u8DB3\u63D0\u4F9B\u7684\u6D4B\u8BD5\u51FD\u6570\u7684\u7B2C\u4E00\u4E2A\u5143\u7D20\u7684\u7D22\u5F15\u3002\u82E5\u6CA1\u6709\u627E\u5230\u5BF9\u5E94\u5143\u7D20\u5219\u8FD4\u56DE-1\u3002</p></blockquote></li></ol>',14),r=[p];function t(c,d,s,n,h,u){return i(),l("div",null,r)}var m=e(o,[["render",t]]);export{b as __pageData,m as default};
